import axios from "axios";

const API_URL = `${process.env.REACT_APP_API_BASE_URL}`;

const getAllCases = async params => {

    const token = JSON.parse(localStorage.getItem("token"))

    const response = await axios.get(`${API_URL}/cases`, {headers:{token},params : {...params}})
    return response;
}

const createCase = async (payload) => {
    const token = JSON.parse(localStorage.getItem("token"))
    const response = await axios.post(`${API_URL}/cases`, payload , {
        headers:{token},
    })
    return response;
};



const updateCase = async (payload) => {
    const token = JSON.parse(localStorage.getItem("token"))
    return axios
        .put(`${API_URL}/${payload.case_id}`, payload , {
            headers:{token},
        })
        .then(response => {
            return response.data;
        })
        .catch(err => {
            return err;
        })
};


const getCaseStatuses = async () => {

    const token = JSON.parse(localStorage.getItem("token"))

    const response = await axios.get(`${API_URL}/definitions/caseStatuses`, {headers:{token}})
    return response;
}

const getPartyTypes = async () => {

    const token = JSON.parse(localStorage.getItem("token"))

    const response = await axios.get(`${API_URL}/definitions/partyTypes`, {headers:{token}})
    return response;
}

const getCaseTypes = async () => {

    const token = JSON.parse(localStorage.getItem("token"))

    const response = await axios.get(`${API_URL}/definitions/caseTypes`, {headers:{token}})
    return response;
}

const getClaimDivisions = async () => {

    const token = JSON.parse(localStorage.getItem("token"))

    const response = await axios.get(`${API_URL}/definitions/claimDivisions`, {headers:{token}})
    return response;
}



export default {
    createCase,
    updateCase,
    getAllCases,
    getCaseStatuses,
    getPartyTypes,
    getCaseTypes,
    getClaimDivisions
};
