import React, { useEffect } from 'react'

import { useDispatch } from 'react-redux'
import Loader from '../components/Loader';


export default function ScreenB2() {
  const dispatch = useDispatch();
  const [showLoader , setShowLoader]  = useState(false)

  const submitHandler = () => {
    setShowLoader(true)
    dispatch(submitChoices()).then(response => {
      setShowLoader(false)
      setNextScreen("ScreenC2");
    })
  }

  if(nextScreen)
    return <ScreenC2 />

  return (
    <div>
      {
        !showLoader ? (
          <>
            <input type="radio" name="choice" value="a" /> Choice A
            <input type="radio" name="choice" value="b" /> Choice B
            <input type="radio" name="choice" value="c" /> Choice C
            <input type="radio" name="choice" value="d" /> Choice D
            <input type="radio" name="choice" value="e" /> Choice E

            <input type="button" onClick={submitHandler} value="Submit"/>
          </>
        ) : <Loader />
      }
    </div>
  )
}
